<script>
export default {
  name: "CemeterySection",
  mounted() {
    // 分割成單個字符
    const ghostText = new SplitType(".split_text");
    
    gsap.registerPlugin(ScrollTrigger);

    const tlGhost = gsap.timeline();

    tlGhost.from(".cemetery_text .char", {
      filter: "blur(20px)",
      willchange: "filter",//優化動畫性能
      stagger: 0.07,
      scrollTrigger: {
        trigger: ".cemetery_text",
        start: "top center",
        end: "bottom top+=300",
        scrub: 1.5,
        // markers: true,
        ease: "power4.out",
        // pin: true
      }
    });

    // Initialize Lenis for smooth scrolling
    const lenis = new Lenis();

    function raf(time) {
      lenis.raf(time);
      requestAnimationFrame(raf);
    }

    requestAnimationFrame(raf);

    const envelope = document.querySelector(".envelope_wrapper");
    // const sticker = document.querySelector(".sticker");
    envelope.addEventListener("click", () => {
      envelope.classList.toggle("flap");
    })

    window.addEventListener("scroll", this.handleScroll);
  },
  //移除之前添加的滾動事件監聽
  beforeUnmount() {
    window.removeEventListener("scroll", this.handleScroll);
  },
  methods: {
    handleScroll() {
      let value = window.scrollY;

      this.$refs.cemeteryText.style.marginTop =`translateX(${value * 0.7}px)`;
      this.$refs.treeLeft.style.transform = `translateX(-${value * 0.9}px)`;
      this.$refs.treeRight.style.transform =`translateX(${value * 0.9}px)`;
      this.$refs.gateLeft.style.transform = `translateX(${value * 0.2}px)` ;
      this.$refs.gateRight.style.transform = `translateX(-${value * 0.2}px)` ;
    },
  }, 
};



</script>

