<script>
export default {
  name: "CemeterySection",
  mounted() {
    // Split text into characters for animation
    const ghostText = new SplitType(".split_text");
    
    // Register GSAP plugin
    gsap.registerPlugin(ScrollTrigger);
    
    // Create GSAP timeline
    const tlGhost = gsap.timeline();

    // Set up scroll-triggered animation
    tlGhost.from(".cemetery_text .char", {
      filter: "blur(20px)",
      willchange: "filter",
      stagger: 0.07, // interval between animations
      scrollTrigger: {
        trigger: ".cemetery_text",
        start: "top center",
        end: "bottom top+=300", // ends when the bottom of the element is 300px above the top of the viewport
        scrub: 1.5,
        // markers: true, // Uncomment for debugging
        ease: "power4.out",
        // pin: true // Uncomment to pin the element
      }
    });

    // Initialize Lenis for smooth scrolling
    const lenis = new Lenis();

    function raf(time) {
      lenis.raf(time);
      requestAnimationFrame(raf);
    }

    requestAnimationFrame(raf);

    // Add scroll event listener
    window.addEventListener("scroll", this.handleScroll);
  },
  beforeUnmount() {
    // Remove scroll event listener
    window.removeEventListener("scroll", this.handleScroll);
  },
  methods: {
    handleScroll() {
      let value = window.scrollY;

      this.$refs.cemeteryText.style.marginTop = value * 0.7 + "px";
      this.$refs.treeLeft.style.left = value * -0.9 + "px";
      this.$refs.treeRight.style.left = value * 0.9 + "px";
      this.$refs.gateLeft.style.left = value * 0.2 + "px";
      this.$refs.gateRight.style.left = value * -0.2 + "px";
    },
  },
};
</script>

